    public static int solve(String A) {
        A = A.toLowerCase();
        int count = 0;
        int n = A.length();
        char[] a = A.toCharArray();
        for(int i=0;i<n;i++){
            if(a[i]=='a' || a[i]=='e' || a[i]=='i' || a[i]=='o' || a[i]=='u'){
                count = count + (n-i);
            }
        }
        return count % 10003;
    }
